import spacy
# we need the model on each worker but do not want to load it for each row. So we use a global variable -> only one load per worker 
NLP = None
def get_spacy_magic_for(lang):
    global NLP
    if NLP is None:
        NLP = {}
        if lang not in NLP:
            NLP[lang] = spacy.load(lang)
    return NLP[lang]

def tokenize_and_stopword_removal(text):
    nlp = get_spacy_magic_for("en_core_web_sm")
    doc = nlp(text)
    return [token.lemma_ for token in doc if not token.is_stop and not token.lemma_ == 'the' and not token.is_punct and not token.is_space and not token.like_num]

tokenize_and_stopword_removal_udf = udf(tokenize_and_stopword_removal, ArrayType(StringType()))
